import React, { useState } from "react";

import { AppBar, Button, MenuList, MenuListItem, Separator, TextInput, Toolbar, styleReset } from 'react95';
import { createGlobalStyle, ThemeProvider } from 'styled-components';

/* Pick a theme of your choice */
import original from 'react95/dist/themes/original';

/* Original Windows95 font (optional) */
import ms_sans_serif from 'react95/dist/fonts/ms_sans_serif.woff2';
import ms_sans_serif_bold from 'react95/dist/fonts/ms_sans_serif_bold.woff2';

const GlobalStyles = createGlobalStyle`
  ${styleReset}
  @font-face {
    font-family: 'ms_sans_serif';
    src: url('${ms_sans_serif}') format('woff2');
    font-weight: 400;
    font-style: normal
  }
  @font-face {
    font-family: 'ms_sans_serif';
    src: url('${ms_sans_serif_bold}') format('woff2');
    font-weight: bold;
    font-style: normal
  }
  body, input, select, textarea {
    font-family: 'ms_sans_serif';
  }
`;


export default ({handleClose}) => {
  const [open, setOpen] = useState(false);

  return (
    <div style={{height: '100%'}}>
    <ThemeProvider theme={original}>
      {/* put screen here, make app par just @ bottom */}
      <div id="windows-background" style={{
        height: '92%',
        padding: '20px',
      }}>
        <div style={{        textAlign: 'center', width: 'max-content'
}}>
          <img width="60px" src="/bin.png"></img><br />
          Recycling Bin
        </div>
      </div>
      <AppBar style={{position: 'relative', bottom: '0px'}} position="inherit">
      <Toolbar style={{ justifyContent: 'space-between' }}>
        <div>
          <Button
            onClick={() => setOpen(!open)}
            active={open}
            style={{ fontWeight: 'bold', width: '110px' }}
          >
            <img
              src="/windows_logo.png"
              alt='windows logo'
              style={{ height: '20px', marginRight: 10 }}
            />
            Start
          </Button>
          {open && (
            <MenuList
              style={{
                position: 'absolute',
                left: '0',
                bottom: '65%',
                width: '130px'
              }}
              onClick={() => setOpen(false)}
            >
              <MenuListItem>
                <span role='img' aria-label='üåê'>
                üåê
                </span>
                Netscape
              </MenuListItem>
              <MenuListItem>
                <span role='img' aria-label='üì´'>
                üì´
                </span>
                AOL Mail
              </MenuListItem>
              <MenuListItem disabled>
                <span role='img' aria-label='üë®‚Äçüíª'>
                  <b>&gt;_</b>
                </span>
                cmd.exe
              </MenuListItem>
              <Separator />
              <MenuListItem onClick={() => {var audio = new Audio('/shutoff.mp3');audio.play();setTimeout(()=>{handleClose()}, 300)}}>
                <span role='img' aria-label='‚èª'>
                 <b>‚èª</b>
                </span>
                Power-off
              </MenuListItem>
            </MenuList>
          )}
        </div>
        <div style={{marginRight: '10px'}}>{(new Date()).toLocaleTimeString()}</div>
      </Toolbar>
    </AppBar>
    </ThemeProvider>
  </div>
  )
}
